#include <iostream>
#include <filesystem>
#include <thread>
#include "Manager.hpp"
#include "Settings.hpp"

int main (int argc, char* argv[]) {
    if (argc < 2 || std::filesystem::exists(argv[1])) {
        std::cerr << "Error: No settings file provided." << std::endl;
        return 1;
    }

    Settings settings (argv[1]);
    try {
        settings.init();
    } catch (...) {
        std::cerr << "Error: Settings file could not be read." << std::endl;
        return 1;
    }

    // Evenly distribute the number of images to be generated by each thread
    uint numPerThread = settings.get_num_nft() / std::thread::hardware_concurrency(); 
    uint extraImg = settings.get_num_nft() % std::thread::hardware_concurrency();
    
    Manager m (settings);
    m.init_threads(numPerThread, extraImg);

    return 0;
}